sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var message = 'OneDrive Flow Not Run';
	// implement resource here
	var incoming = request.body.data;
	var flowData = {
		access_url : incoming['access_url'],
		status : incoming['status'],
		ritm : incoming['ritm'],
		is_test : incoming['is_test']
	};
	var notRunReasons = [];
	if (gs.nil(flowData.access_url)) notRunReasons.push('Blank URL');
	if (gs.nil(flowData.status)) notRunReasons.push('Blank Status');
	if (gs.nil(flowData.ritm)) notRunReasons.push('Blank RITM');
	if (flowData.status != "Success" && flowData.status != "success") notRunReasons.push('Status Not Success');

	if(notRunReasons.length == 0)
	{
		try {
			message = 'OneDrive Data Accepted';
			var inputs = {};
			inputs['flowdata'] = flowData; // Object 
			var contextId = sn_fd.FlowAPI.startSubflow('x_apig_termination.onedrive_delegation_return_data', inputs);

			//Set value of onedrive_flow_sysid to this context sys_id
			var ritmGr = new GlideRecord('sc_req_item');
			ritmGr.get(flowData.ritm);
			var ritmId = ritmGr.sys_id.toString();

			var mtomGr = new GlideRecord('sc_item_option_mtom');
			mtomGr.addEncodedQuery('sc_item_option.item_option_new=59a3b5a81bea21908c5143f3cc4bcba2^request_item=' + ritmId);
			mtomGr.query();
			if(mtomGr.next()){
			var option = mtomGr.sc_item_option.sys_id.toString();

			var optionGr = new GlideRecord('sc_item_option');
			optionGr.get(option);
			optionGr.setValue('value',contextId);
			optionGr.update();
			}
		} catch (e) {
			//If fatal error then create incident
			var incidentGr = new GlideRecord('incident');
			incidentGr.initialize();
			incidentGr.short_description = 'Azure to APi Connect API Failure(Exception)';
			incidentGr.description = e;
			incidentGr.assigned_to = '';
			incidentGr.assignment_group = '48b385421be35090e7004229bc4bcb14';
			incidentGr.category = 'Fault/Failure';
			incidentGr.subcategory = 'Hardware';
			incidentGr.u_service = 'APi Connect (ServiceNow)';
			incidentGr.insert();
		}
	}
	else{
		message = 'OneDrive Flow Not Run. Reason';
		message += notRunReasons.length > 1 ? 's: ' : ': ';
		message += notRunReasons.join(',');
	}
	//Sending Response
	var body = {};
	body.status = "OK";
	body.msg = message;
	response.setBody(body);
})(request, response);
